context.value:
align-self: flex-start;
  background-color: transparent;
  border: 0;
  color: $sprk-alert-dismiss-icon-color;
  padding: $sprk-alert-dismiss-padding;

background-color: $sprk-btn-secondary-background-color;
  border: $sprk-btn-border-width $sprk-btn-border-style
    $sprk-btn-secondary-border-color;
  color: $sprk-btn-secondary-text-color;
  letter-spacing: $sprk-btn-secondary-letter-spacing;

  &:visited {
    color: $sprk-btn-secondary-text-color;
  }

  &:hover,
  &:active,
  &:focus {
    box-shadow: $sprk-btn-secondary-hover-shadow;
    background-color: $sprk-btn-secondary-hover-background-color;
    border: $sprk-btn-border-width $sprk-btn-border-style
      $sprk-btn-secondary-hover-border-color;
    color: $sprk-btn-secondary-hover-text-color;
  }

background-color: $sprk-btn-tertiary-background-color;
  box-shadow: none;
  border: $sprk-btn-tertiary-border;
  color: $sprk-btn-tertiary-text-color;
  letter-spacing: $sprk-btn-tertiary-letter-spacing;

  &:hover,
  &:active,
  &:focus {
    background-color: $sprk-btn-tertiary-hover-background-color;
    color: $sprk-btn-tertiary-hover-text-color;
    box-shadow: $sprk-btn-tertiary-hover-shadow;
  }

  &:visited {
    color: $sprk-btn-tertiary-text-color;
  }

  &::after {
    content: '';
    border-bottom: $sprk-btn-tertiary-underline-width solid
      $sprk-btn-tertiary-underline-color;
    display: block;
    width: 100%;
    padding-top: $sprk-btn-tertiary-underline-padding-top;
    margin: 0 auto;
  }

  &:hover::after,
  &:active::after,
  &:focus::after {
    border-color: $sprk-btn-tertiary-hover-underline-color;
    transition: border $sprk-btn-transition-speed ease-in-out;
  }

position: absolute;
  bottom: $sprk-space-l;
  left: $sprk-space-l;
  max-width: $sprk-highlight-board-content-width;
  right: $sprk-space-l;
  text-align: left;

position: absolute;
  bottom: $sprk-space-l;
  left: $sprk-space-l;
  max-width: $sprk-highlight-board-content-width;
  right: $sprk-space-l;
  text-align: left;

position: absolute;
  bottom: $sprk-space-l;
  left: $sprk-space-l;
  max-width: $sprk-highlight-board-content-width;
  right: $sprk-space-l;
  text-align: left;

position: fixed;
  left: 0;
  right: 0;
  height: 100%;

  @media all and (min-width: $sprk-masthead-breakpoint) {
    position: sticky;
    height: auto;
  }

background-color: $sprk-masthead-accordion-item-open-line-background-color;
  content: '';
  display: block;
  height: $sprk-masthead-accordion-item-open-line-height;
  margin: 0 auto;
  width: 100%;

.sprk-c-Stepper__step-heading,
  .sprk-c-Stepper__step--selected
  .sprk-c-Stepper__step-heading {
    color: $sprk-stepper-dark-step-heading-color;
  }

  .sprk-c-Stepper__step::before {
    background-color: $sprk-stepper-dark-bar-color;
  }

  .sprk-c-Stepper__step-icon {
    background-color: $sprk-stepper-dark-icon-color;
    border-color: $sprk-stepper-dark-icon-border-color;
  }

  .sprk-c-Stepper__step--selected
  .sprk-c-Stepper__step-content--has-description
  .sprk-c-Stepper__step-heading {
    color: $sprk-stepper-dark-step-description-selected;
  }

  .sprk-c-Stepper__step--selected
  .sprk-c-Stepper__step-icon,
  .sprk-c-Stepper__step--selected
  .sprk-c-Stepper__step-header:hover
  .sprk-c-Stepper__step-icon {
    background-color: $sprk-stepper-dark-icon-color-selected;
  }

  .sprk-c-Stepper__step-header:hover
  .sprk-c-Stepper__step-icon {
    background-color: $sprk-stepper-dark-icon-color-hover;
  }

  .sprk-c-Stepper__step--selected
  .sprk-c-Stepper__step-content--hidden {
    background-color: transparent;
    box-shadow: none;
  }

.sprk-c-Stepper__step-heading,
  .sprk-c-Stepper__step--selected
  .sprk-c-Stepper__step-heading {
    color: $sprk-stepper-dark-step-heading-color;
  }

  .sprk-c-Stepper__step::before {
    background-color: $sprk-stepper-dark-bar-color;
  }

  .sprk-c-Stepper__step-icon {
    background-color: $sprk-stepper-dark-icon-color;
    border-color: $sprk-stepper-dark-icon-border-color;
  }

  .sprk-c-Stepper__step--selected
  .sprk-c-Stepper__step-content--has-description
  .sprk-c-Stepper__step-heading {
    color: $sprk-stepper-dark-step-description-selected;
  }

  .sprk-c-Stepper__step--selected
  .sprk-c-Stepper__step-icon,
  .sprk-c-Stepper__step--selected
  .sprk-c-Stepper__step-header:hover
  .sprk-c-Stepper__step-icon {
    background-color: $sprk-stepper-dark-icon-color-selected;
  }

  .sprk-c-Stepper__step-header:hover
  .sprk-c-Stepper__step-icon {
    background-color: $sprk-stepper-dark-icon-color-hover;
  }

  .sprk-c-Stepper__step--selected
  .sprk-c-Stepper__step-content--hidden {
    background-color: transparent;
    box-shadow: none;
  }

.sprk-c-Stepper__step-description {
    display: block;
  }

  .sprk-c-Stepper__step-heading {
    font-weight: $sprk-stepper-step-heading-font-weight;
    color: $sprk-stepper-step-heading-color-selected;
  }

  .sprk-c-Stepper__step-icon {
    background-color: $sprk-stepper-icon-color-selected;
    border-color: $sprk-stepper-icon-border-color-selected;
    box-shadow: $sprk-stepper-icon-box-shadow-selected;
  }

  .sprk-c-Stepper__step-header:hover
  .sprk-c-Stepper__step-icon {
    background-color: $sprk-stepper-icon-color-selected;
  }

  &.sprk-c-Stepper__step--first::before,
  &.sprk-c-Stepper__step--has-slider.sprk-c-Stepper__step--first::before {
    height: 100%;
    top: ($sprk-stepper-step-padding + $sprk-stepper-icon-box-shadow-selected-spread) + ($sprk-stepper-icon-height / 2);
  }

  &.sprk-c-Stepper__step--last::before,
  &.sprk-c-Stepper__step--has-slider.sprk-c-Stepper__step--last::before {
    height: ($sprk-stepper-step-padding + $sprk-stepper-icon-box-shadow-selected-spread);
  }

  .sprk-c-Stepper__step-content {
    background-color: $sprk-stepper-step-content-bg-selected;
  }

  .sprk-c-Stepper__step-content--has-description {
    background-color: $sprk-stepper-step-description-bg-selected;
    box-shadow: $sprk-stepper-step-description-box-shadow;
  }
  /// Adds styles to hide the step content.
  .sprk-c-Stepper__step-content--hidden {
    background-color: transparent;
    box-shadow: none;
  }

.sprk-c-Stepper__slider {
    position: static;

    @media (min-width: $sprk-stepper-breakpoint) {
      position: absolute;
    }
  }

  .sprk-c-Stepper__step {
    display: none;

    @media (min-width: $sprk-stepper-breakpoint) {
      display: flex;
    }
  }

  .sprk-c-Stepper__step-content {
    margin-left: 0;

    @media (min-width: $sprk-stepper-breakpoint) {
      margin-left: $sprk-stepper-step-content-margin-left;
    }
  }
